import React, { useState } from 'react';
import Timeline from './Timeline/Timeline';
import About from './About/About';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

import './UserProfile.css';

export default function UserProfile() {
    // set the states for the styling of the timeline and about me navs
    const [timelineStyle, setTimelineStyle] = useState("nav-link active");
    const [aboutStyle, setAboutStyle] = useState("nav-link");

    // func to set the about nav button to be active
    function onAboutMe() {
        setTimelineStyle("nav-link");
        setAboutStyle("nav-link active");
    }

    // func to set the timeline nav button to be active
    function onTimeLine() {
        setTimelineStyle("nav-link active");
        setAboutStyle("nav-link");
    }

    return (
        <>
            <Router>
                <div className="container userprofile-wrapper">
                    <img src='favicon.ico' className="rounded mx-auto d-block" alt="..." />
                    <div className="userprofile_name">
                        <p className="username">Jimmy Tran</p>
                        <a href="">Add Bio</a>
                        <hr />
                    </div>
                    <nav className="navbar navbar-expand-lg navbar-light bg-white">
                        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarText" aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse user-nav-bar" id="navbarText">
                            <ul className="navbar-nav mr-auto">
                                <li className={timelineStyle}>
                                    <Link onClick={onTimeLine} className="nav-link" to="/">Timeline</Link>
                                </li>
                                <li className={aboutStyle}>
                                    <Link onClick={onAboutMe} className="nav-link" to="/aboutme">About</Link>
                                </li>
                            </ul>
                        </div>
                    </nav>
                    <hr />
                </div>
                <div className="about-content-container">
                    <div className="container">
                        {/* Switch for rendering About and Timeline components */}
                        <Switch>
                            <Route path="/aboutme">
                                <About />
                            </Route>
                            <Route path="/">
                                <Timeline />
                            </Route>
                        </Switch>
                    </div>
                </div>
            </Router>
        </>
    );
}
